categoria.map:
function(doc) { 
  if( doc.type == 'categoria' ) { emit( [doc.label, 0], { "label" : doc.label, "value" : doc.id } )  
 } 
 }

examen_categoria.map:
function(doc) { 
 if( doc.type == 'categoria') { 
 emit([doc._id, 0], doc ) 
 } 
 if( doc.type == 'examen'){ 
 emit([doc.categoria,1], doc) 
 } 
 }
